package LeetCode.problem;import java.util.Queue;/** * @author: lingjunhao * create at:  19/3/12  下午3:29 * @description: 实现对列 */public class Num004_answer {    private Queue<Num004_NewClass> dogQ;    private Queue<Num004_NewClass> catQ;    private Long count;    public Num004_answer() {        this.dogQ = dogQ;        this.catQ = catQ;        this.count = count;    }    public void add(Num004 num004){        if (num004.getType().equals("dog")){            this.dogQ.add(new Num004_NewClass(num004,this.count++));        }else if (num004.getType().equals("cat")){            this.catQ.add(new Num004_NewClass(num004,this.count++));        }else {            throw new RuntimeException("err!!!");        }    }    public Num004 popAll(){        if (!this.dogQ.isEmpty()&&!this.catQ.isEmpty()){            if (this.dogQ.peek().getCount()<this.catQ.peek().getCount()){                return this.dogQ.poll().getNum004();            }else {                return this.catQ.poll().getNum004();            }        }else if (!this.dogQ.isEmpty()){            return this.dogQ.poll().getNum004();        }else if (!this.catQ.isEmpty()){            return this.catQ.poll().getNum004();        }else {            throw new RuntimeException("error");        }    }}