package LeetCode.problem;/** * @author: lingjunhao * create at:  19/4/10  上午9:39 * @description: 反向整数 */public class Reverse_Integer {    public int reverse(int x) {        int rev=x;        int k=0;        String xStr="";        while (x!=0){            int low=rev%10;            x/=10;            if (low!=0&&k!=0){                xStr+=String.valueOf(low);            }            k++;        }        return Integer.parseInt(xStr);    }}