package Offer_week1;/** * @author: lingjunhao * create at:  19/4/14  ä¸‹åˆ1:48 * @description: */public class Solution2{    public int duplicateInArray(int[] nums) {        int l=1;        int r=nums.length-1;        while (r>=l){            int mid=(r-l)/2+l;            int count=getCount(nums,l,mid);            if (l == r) {                if (count > 1)                    return l;                else                    break;            }            if (count > mid - l + 1) {                r = mid;            }            else                l = mid + 1;        }        return -1;    }    public static int getCount(int[] arr, int start, int end) {        int count = 0;        for (int i = 0; i < arr.length; i++) {            if (arr[i] >= start && arr[i] <= end) count++;        }        return count;    }}