package Offer_week1;/** * @author: lingjunhao * create at:  19/4/18  下午8:41 * @description: 替换空格 */public class Solution4 {    public static String replaceBlank(String str){        StringBuilder strb=new StringBuilder();        for (int i = 0; i < str.length(); i++) {            if (str.charAt(i)==' '){                strb.append("%20");            }            else                strb.append(str.charAt(i));        }        return strb.toString();    }}