package Offer_week1;/** * @author: lingjunhao * create at:  19/4/20  下午3:25 * @description: 重建二叉树 *///class TreeNode {//       int val;//       TreeNode left;//       TreeNode right;////       TreeNode(){}//       TreeNode(int x) { val = x; }//   }public class Solution6 {    public TreeNode buildTree(int[] preorder, int[] inorder) {        TreeNode root=buildTree(preorder,0,preorder.length-1,inorder,0,inorder.length-1);        return root;    }    public static TreeNode buildTree(int[] preorder,int startPre,int endPre,int []inorder,int startIn,int endIn){        if (startPre>endPre||startIn>endIn){            return null;        }        TreeNode root=new TreeNode(preorder[startPre]);//        root.val=preorder[startPre];        for (int i=startIn;i<=endIn;i++){            if (inorder[i]==preorder[startPre]){                root.left=buildTree(preorder,                        startPre+1,                        startPre+i-startIn,                        inorder,                        startIn,                        i-1);                root.right=buildTree(preorder,                        startPre+i-startIn+1,                        endPre,                        inorder,                        i+1,                        endIn);            }        }        return root;    }}