package Offer_week2;/** * @author: lingjunhao * create at:  19/4/21  上午10:45 * @description: 机器人的运动轨迹 */public class Solution1 {    public int movingCount(int threshold, int rows, int cols)    {        boolean[][] visited=new boolean[rows][cols];        return backtrack(threshold,rows,cols,0,0,visited);    }    public static int backtrack(int threshole,int rows,int cols,int x,int y,boolean[][] visited){        if (x<0||x>=rows||y<0||y>=cols||visited[x][y]||getcoount(x)+getcoount(y)>threshole)            return 0;        visited[x][y]=true;        return  backtrack(threshole,rows,cols,x-1,y,visited)+                backtrack(threshole,rows,cols,x,y-1,visited)+                backtrack(threshole,rows,cols,x+1,y,visited)+                backtrack(threshole,rows,cols,x,y+1,visited)+1;    }    public static int getcoount(int x){        int count=0;        while (x!=0){            count+=x%10;            x/=10;        }        return count;    }}