package Offer_week2;/** * @author: lingjunhao * create at:  19/4/21  下午10:34 * @description: 链表倒数第k个节点 */public class Solution10 {    public ListNode findKthToTail(ListNode pListHead, int k) {        if (pListHead==null)return null;        ListNode p=pListHead;        int len=0;        while (p!=null){            p=p.next;            ++len;        }        ListNode q=pListHead;        if(k>len)return null;        for (int i = 0; i < len-k; i++) {            q=q.next;        }        return q;    }    public static void main(String[] args) {    }}