package Offer_week2;/** * @author: lingjunhao * create at:  19/4/21  下午12:40 * @description: 二进制中1的个数 */public class Solution3 {    public int NumberOf1(int n)    {        String s=Integer.toBinaryString(n);        int count=0;        for (int i = 0; i < s.length(); i++) {            if (s.charAt(i)=='1')count++;        }        return count;    }    public static void main(String[] args) {        Solution3 solution3=new Solution3();        solution3.NumberOf1(-2);    }}