package Offer_week2;/** * @author: lingjunhao * create at:  19/4/21  下午1:19 * @description: 删除链表中重复的节点 */public class Solution6 {    public ListNode deleteDuplication(ListNode head) {        ListNode p=new ListNode(0);        p.next=head;        ListNode q=p;        while (q.next!=null) {            ListNode sol = q.next;            while (sol!=null&&sol.val==q.next.val)                sol=sol.next;            if (q.next.next==sol)q=q.next;            else q.next=sol;        }        return p.next;    }}