package Offer_week3;/** * @author: lingjunhao * create at:  19/4/25  下午5:09 * @description: 反转链表 */class ListNode {      int val;      ListNode next;      ListNode(int x) { val = x; }}public class Solution1 {    public ListNode reverseList(ListNode head) {        if (head==null)return null;        ListNode pre=null;        ListNode next=null;        while (head!=null){            next=head.next;            head.next=pre;            pre=head;            head=next;        }        return pre;    }}