package Offer_week3;import java.util.ArrayList;import java.util.LinkedList;import java.util.List;import java.util.Queue;/** * @author: lingjunhao * create at:  19/5/2  上午10:50 * @description: 之字型打印二叉树 */public class Solution11 {    public List<List<Integer>> printFromTopToBottom(TreeNode root) {        List<List<Integer>> list=new ArrayList<>();        int key=0;        if (root==null)return list;        Queue<TreeNode> q=new LinkedList<TreeNode>();        q.offer(root);        while (q.size()!=0){            int size=q.size();            LinkedList<Integer> p=new LinkedList<>();            while (size-->0){                TreeNode res=q.poll();                if (res==null)continue;                if (key%2==0)                    p.addLast(res.val);                else                    p.addFirst(res.val);                q.offer(res.left);                q.offer(res.right);            }            key++;            if (p.size()>0)list.add(p);        }        return list;    }}