package Offer_week3;/** * @author: lingjunhao * create at:  19/4/25  下午5:41 * @description: 二叉树的镜像 */public class Solution4 {    public void mirror(TreeNode root) {        TreeNode x=null;        if (root!=null){            x=root.left;            root.left=root.right;            root.right=x;            mirror(root.left);            mirror(root.right);        }    }}