package Offer_week3;/** * @author: lingjunhao * create at:  19/4/25  下午7:12 * @description: 对称的二叉树 */public class Solution5 {    public boolean isSymmetric(TreeNode root) {        return isSymmetric(root,root);    }    public static boolean isSymmetric(TreeNode root1,TreeNode root2){        if (root1==null&&root2==null)return true;        if (root1==null||root2==null)return false;        if (root1.val!=root2.val){            return false;        }        return isSymmetric(root1.left,root2.right)&&isSymmetric(root1.right,root2.left);    }}