package Offer_week3;import java.util.Stack;/** * @author: lingjunhao * create at:  19/4/25  下午8:22 * @description: 包含min函数的栈 */class MinStack{    Stack<Integer> stack;    Stack<Integer> min;    public MinStack(){        stack=new Stack<>();        min=new Stack<>();    }    public void push(int x){        stack.push(x);        if (min.isEmpty()){            min.push(x);        }else {            Integer peek=min.peek();            if (x<=peek){                min.push(x);            }else {                min.push(peek);            }        }    }    public void pop(){        stack.pop();        min.pop();    }    public int top(){        return stack.peek();    }    public int getMin(){        return min.peek();    }}public class Solution7 {}