package Offer_week3;import java.util.ArrayList;import java.util.LinkedList;import java.util.List;import java.util.Queue;/** * @author: lingjunhao * create at:  19/4/30  上午10:09 * @description: 不分行从上到下打印二叉树 */public class Solution9{    public List<Integer> printFromTopToBottom(TreeNode root) {        List<Integer> list=new ArrayList<>();        Queue<TreeNode> q=new LinkedList<>();        if (root==null){            return list;        }        q.offer(root);        while (q.size()!=0){            TreeNode tmp=q.peek();            q.poll();            list.add(tmp.val);            if (tmp.left!=null){                q.offer(tmp.left);            }if (tmp.right!=null){                q.offer(tmp.right);            }        }        return list;    }}