package T_0x00;import java.util.Scanner;/** * @author: lingjunhao * create at:  19/5/6  上午11:46 * @description: */public class Solution1 {    public static Long fastPower(Long a,Long b,Long p){        Long ans=1l;        while (b>0){            if (b%2>0)ans=(ans*a)%p;            a=(a*a)%p;            b/=2;        }        return ans%p;    }    public static void main(String[] args) {        Long a,b,p;        Scanner scanner=new Scanner(System.in);        a=scanner.nextLong();        b=scanner.nextLong();        p=scanner.nextLong();        System.out.println(fastPower(a,b,p));    }}