package text_java_interview;import java.util.Scanner;/** * @author: lingjunhao * create at:  19/5/1  下午4:39 * @description: */public class Solution2 {    public static void main(String[] args) {        Scanner scanner=new Scanner(System.in);        int x=scanner.nextInt();        int res=0;        if (x<=5)            res+=1;        else {            if (x%5==0){                res=x/5;            }else {                res=x/5+1;            }        }        System.out.println(res);    }}